// Из библиотеки UI-компонентов возьмите следующие:
//     иконки,

//     систему отступов.
// У компонента свой кастомизированный скроллбар, 
//в библиотеке для него есть готовый класс custom-scroll.
//Подумайте над возможным ограничением высоты блока, 
//в том числе и на разных разрешениях экранов. 


import React from 'react';

import styles from "./BurgerIngredients.module.css";
import { data } from "../../utils/data";
import { useState } from "react";

import {Tab} from "@ya.praktikum/react-developer-burger-ui-components/dist/index.js";
import {Counter} from "@ya.praktikum/react-developer-burger-ui-components/dist/index.js";
import {CurrencyIcon} from "@ya.praktikum/react-developer-burger-ui-components/dist/index.js";

const TabList = () => {
  const [current, setCurrent] = React.useState('Булки');
  return(
    <div style={{display:"flex"}}>
      <Tab value="Булки" active={current === 'Булки'} onClick={setCurrent}>
          Булки
      </Tab>
      <Tab value="Соусы" active={current === 'Соусы'} onClick={setCurrent}>
          Соусы
      </Tab>
      <Tab value="Начинка" active={current === 'Начинка'} onClick={setCurrent}>
          Начинка
      </Tab>
    </div>
  )
}

const Ingredients = (props) => {

    if(obj.type == props.selector){
      return(
        <li className={styles.card}>
          <img src={obj.image}/>
          <p className='text text_type_digits-default'>20 <CurrencyIcon type="primary" /></p>
          <p className='text text_type_main-default'>{obj.name}</p>
        </li>
      )
    } 
}


export default class BurgerIngredients extends React.Component{
render()
{return(

  <div>

    <h2 className="text text_type_main-large">Собери бургер</h2>
    <TabList/>
    <div>

      <ul className={styles.list}>
        <li className='text text_type_main-medium'>Булки</li>
        
        {data.forEach((obj) => {<Ingredients selector="bun"/>})}

        
      </ul>

      <ul className={styles.list}>
        <li className='text text_type_main-medium'>Соусы</li>
        <li></li>
      </ul>

      <ul className={styles.list}>
        <li className='text text_type_main-medium'>Начинка</li>
        <li></li>
      </ul>

    </div>





   
      
  </div>
  );}
}
